section.section:
struct A
{
	template<class T>
	class B;
	// GCC - Error.
	template<>
	class B<void> {};
};
Workaround:
struct A
{
	template<class T, class = void>
	class B;
	template<class D>
	class B<void, D> {};
};

class or; // GCC - error.
class and; // GCC - error.
class not; // GCC - error.
Workaround:
class or_;
class and_;
class not_;

class A
{
	class B
	{
	public:
		B(A a) 
		{ 
			a.c; // DMC - error.
		}
	};
private:
	int c;
};
Workaround:
class A
{
	class B
	{
	public:
		B(A a) 
		{ 
			a.c;
		}
	};
	friend class B;
private:
	int c;
};

section.pre:
template<class A>
class B
{
public:
	static const int value = 0;
};
// DMC - error.
template<class A, int D = B<A>::value>
class C
{
};

template<class T>
class A
{
public:
  friend T D()
	// DMC - error.
  {
    return T();
  }
};

class B: public A<B>
{
};

section.section:
template<class C, class T1>
void A(T1)
{
}
template<class C, unsigned int S>
void A(const char (&)[S])
{
}
int main()
{
	A<int>("H");
}
Workaround:

const char Qwerty[] = "QWERTY";

template<class M>
void T1(const M &X)
{
}

template<class M>
void T1(M &X)
{
}

int main()
{
	T1(Qwerty); // DMC - error.
}
Workaround:
