ul.li:
Enumerations.
class a: public wrap<a, internal>
{
public:

	enum enum_
	{
		b = B;
		c = C;
	};

	a() {}
	a(enum_ E): wrap<a, internal>(E) {}
};

Windows structures.
template<class T>
class windows_structure: public T
{
public:
	windows_structure()
	{
		this->cbSize = sizeof(T);
	}
};

typedef windows_structure<A> a;

Dynamic array on the end of structures.
template<class T>
class dynamic_structure
{
public:
	dynamic_structure(std::size_t Size): Internal(Size) 
	{
		new(&*Internal.begin()) T();
	}	
private:
	std::vector<byte> Internal;
};

struct A
{
	DWORD cbSize;
	DWORD Len;
	CHAR Array[0];
};

typedef dynamic_structure<windows_structure<A> > a;

Optional reference.
template<class T>
class optional_ref: public wrap<T *>
{
public:
	optional_ref() {}
	optional_ref(T &X): wrap(&X) {}
};
